import React, { useState, useEffect } from "react";
import axios from "axios";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Link,
  useParams,
  useNavigate,
  useLocation,
} from "react-router-dom";

// IMPORTANT: API_BASE_URL is set to your local network IP and backend port (8000).
// This will ONLY work if the device accessing the React app (your phone, Mac)
// is on the SAME WI-FI NETWORK as the computer running your FastAPI backend.
//
// If you want your website to work for people OFF your local network,
// you MUST deploy your FastAPI backend to a public server (e.g., a VPS)
// and update this URL to your public server's IP or domain (e.g., https://api.jamiesamazon.deals).
const API_BASE_URL = "http://192.168.0.156:8000"; // <--- Updated based on your local IP and backend port

// Admin token for a simple login mechanism. In a real application,
// this would be replaced by a more secure authentication system (e.g., JWT).
const ADMIN_TOKEN = "letmein";

/**
 * ProductList Component
 * Displays a grid of product cards.
 * @param {Object} props - Component props.
 * @param {Array<Object>} props.products - An array of product objects to display.
 */
const ProductList = ({ products }) => {
  // If no products are available or the products array is empty, display a message.
  if (!products || products.length === 0) {
    return <p className="text-center mt-8 text-gray-600">No products found. Add some from the Admin panel!</p>;
  }

  return (
    <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6 mt-8">
      {products.map((p) => (
        <div
          key={p.id} // Unique key for list rendering, important for React performance
          className="bg-white shadow-md rounded-lg p-4 flex flex-col items-center relative"
        >
          <img src={p.image} alt={p.title} className="w-40 h-auto mb-4 rounded-md" />
          <h3 className="text-lg font-semibold text-center mt-2">{p.title}</h3>
          <p className="text-green-600 font-bold my-2">{p.price}</p>
          <Link
            to={`/product/${p.id}`} // Link to the detailed product page
            className="mt-auto bg-blue-600 hover:bg-blue-700 text-white py-2 px-4 rounded-full transition duration-300 ease-in-out transform hover:scale-105"
          >
            View Product
          </Link>
        </div>
      ))}
    </div>
  );
};

/**
 * ProductPage Component
 * Displays detailed information for a single product.
 */
const ProductPage = () => {
  const { productId } = useParams(); // Get product ID from URL parameters
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true); // State to manage loading status
  const [error, setError] = useState(null); // State to manage error messages

  useEffect(() => {
    // Effect hook to fetch product data when component mounts or productId changes
    setLoading(true); // Set loading to true before fetching
    setError(null); // Clear any previous errors

    axios.get(`${API_BASE_URL}/product/${productId}`)
      .then((res) => {
        // Check if the response contains an error field from the backend
        if (res.data && res.data.error) {
          setError(res.data.error);
        } else {
          setProduct(res.data); // Set product data if successful
        }
      })
      .catch((err) => {
        // Catch network or API call errors
        console.error("Error fetching product details:", err);
        setError("Failed to load product details. Please try again later.");
      })
      .finally(() => {
        setLoading(false); // Set loading to false once fetch is complete (success or error)
      });
  }, [productId]); // Dependency array: re-run effect if productId changes

  // Conditional rendering based on loading, error, or product data
  if (loading) return <p className="text-center mt-10 text-xl text-blue-600">Loading product details...</p>;
  if (error) return <p className="text-center mt-10 text-xl text-red-500">{error}</p>;
  if (!product) return <p className="text-center mt-10 text-xl text-gray-600">Product not found.</p>;

  return (
    <div className="max-w-2xl mx-auto mt-10 p-6 bg-white shadow-xl rounded-lg border-t-4 border-blue-500">
      <h2 className="text-3xl font-extrabold text-gray-800 mb-4 text-center">{product.title}</h2>
      <img
        src={product.image}
        alt={product.title}
        className="w-full max-w-xs mx-auto mb-6 rounded-lg shadow-md"
      />
      <p className="text-2xl text-green-700 font-bold mb-6 text-center">{product.price}</p>
      <a
        href={product.url}
        target="_blank"
        rel="noreferrer noopener" // Added noopener for security best practice
        className="block text-center bg-orange-600 hover:bg-orange-700 text-white py-3 px-6 rounded-full font-semibold text-lg transition duration-300 ease-in-out transform hover:scale-105 shadow-lg"
      >
        Buy on Amazon
      </a>
      <p className="text-xs text-center text-gray-500 mt-4">
        As an Amazon Associate, I earn from qualifying purchases.
      </p>

      {product.review && (
        <div className="mt-8 bg-yellow-50 p-5 rounded-lg border border-yellow-200 shadow-inner">
          <p className="text-sm text-gray-800 italic leading-relaxed">"{product.review}"</p>
        </div>
      )}
    </div>
  );
};

/**
 * AdminLogin Component
 * Provides a simple password-based login for the admin panel.
 */
const AdminLogin = () => {
  const [password, setPassword] = useState("");
  const navigate = useNavigate(); // Hook for programmatic navigation

  const handleLogin = (e) => {
    e.preventDefault(); // Prevent default form submission
    if (password === ADMIN_TOKEN) {
      // Navigate to admin panel, passing the token as a URL parameter
      navigate("/admin?token=" + ADMIN_TOKEN);
    } else {
      // Use an alert for simplicity, consider a custom modal for better UX
      alert("Incorrect password.");
    }
  };

  return (
    <div className="max-w-md mx-auto mt-20 p-8 bg-white shadow-xl rounded-lg border-t-4 border-purple-500">
      <h2 className="text-2xl font-bold text-gray-800 mb-6 text-center">Admin Login</h2>
      <form onSubmit={handleLogin} className="space-y-4">
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          placeholder="Enter admin password"
          className="w-full border border-gray-300 px-4 py-3 rounded-lg focus:ring-2 focus:ring-purple-400 focus:border-transparent transition duration-200"
          aria-label="Admin Password"
        />
        <button
          type="submit"
          className="w-full bg-purple-600 hover:bg-purple-700 text-white py-3 rounded-lg font-semibold text-lg transition duration-300 ease-in-out transform hover:scale-105 shadow-md"
        >
          Login
        </button>
      </form>
    </div>
  );
};

/**
 * AdminPage Component
 * Allows adding and deleting products.
 * @param {Object} props - Component props.
 * @param {Function} props.setProducts - Function to update the main products state in App.
 */
const AdminPage = ({ setProducts }) => {
  const [link, setLink] = useState("");
  const [message, setMessage] = useState(""); // Feedback message for user actions
  const [localProducts, setLocalProducts] = useState([]); // Products specific to admin view
  const [loading, setLoading] = useState(true); // Loading state for admin products
  const [error, setError] = useState(null); // Error state for admin products

  /**
   * Fetches products from the backend for the admin panel.
   */
  const fetchProducts = async () => {
    setLoading(true);
    setError(null);
    try {
      const res = await axios.get(`${API_BASE_URL}/products`);
      // Validate that the response data is an array
      if (Array.isArray(res.data)) {
        setLocalProducts(res.data); // Update local state for admin view
        setProducts(res.data);     // Update global state for main product list
      } else {
        console.error("Backend /products did not return an array for admin panel:", res.data);
        setError("Error: Expected product list but received unexpected data.");
        setLocalProducts([]); // Ensure array is empty to prevent rendering errors
      }
    } catch (err) {
      console.error("Failed to fetch products for admin panel:", err);
      setError("Failed to load products. Check server connection and console for details.");
    } finally {
      setLoading(false);
    }
  };

  // Effect hook to fetch products when the component mounts
  useEffect(() => {
    fetchProducts();
  }, []); // Empty dependency array means this runs once on mount

  /**
   * Handles submission of a new product link.
   * @param {Event} e - The form submission event.
   */
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!link) {
      setMessage("Please enter an Amazon product URL.");
      return;
    }
    setMessage("Adding product... This may take a moment as the product details are being fetched."); // Provide immediate feedback
    try {
      const res = await axios.post(`${API_BASE_URL}/add-product`, {
        url: link,
      });
      if (res.data && !res.data.error) {
        // Add new product to the beginning of the list
        setLocalProducts((prev) => [res.data, ...prev]);
        setProducts((prev) => [res.data, ...prev]);
        setLink(""); // Clear the input field
        setMessage("‚úÖ Product added successfully!");
      } else {
        // Display specific error message from backend if available
        setMessage(`‚ùå Failed to add product: ${res.data.detail || res.data.error || 'Unknown error'}`);
      }
    } catch (err) {
      setMessage("‚ùå Server error when adding product. Check console for network issues or backend errors.");
      console.error("Add product error:", err.response ? err.response.data : err.message);
    }
  };

  /**
   * Handles deletion of a product.
   * @param {string} id - The ID of the product to delete.
   */
  const handleDelete = async (id) => {
    // Confirmation dialog before deleting
    if (!window.confirm("Are you sure you want to delete this product? This action cannot be undone.")) return;
    try {
      await axios.delete(`${API_BASE_URL}/product/${id}`);
      // Filter out the deleted product from both local and global states
      const updated = localProducts.filter((p) => p.id !== id);
      setLocalProducts(updated);
      setProducts(updated);
      setMessage("Product deleted successfully.");
    } catch (err) {
      alert("Failed to delete product."); // Use alert for simplicity, consider modal
      console.error("Delete product error:", err);
      setMessage("‚ùå Failed to delete product.");
    }
  };

  // Conditional rendering for admin panel based on loading/error states
  if (loading) return <p className="text-center mt-10 text-xl text-blue-600">Loading admin panel products...</p>;
  if (error) return <p className="text-center mt-10 text-xl text-red-500">{error}</p>;

  return (
    <div className="max-w-6xl mx-auto mt-10 p-8 bg-white shadow-xl rounded-lg border-t-4 border-green-500">
      <h2 className="text-2xl font-bold text-gray-800 mb-6 text-center">Admin Panel</h2>
      <form onSubmit={handleSubmit} className="flex flex-col sm:flex-row gap-4 mb-6">
        <input
          value={link}
          onChange={(e) => setLink(e.target.value)}
          placeholder="Paste Amazon product URL here..."
          className="flex-1 border border-gray-300 px-4 py-3 rounded-lg focus:ring-2 focus:ring-green-400 focus:border-transparent transition duration-200"
          required // HTML5 validation: field is required
          aria-label="Amazon Product URL"
        />
        <button
          type="submit"
          className="bg-green-600 hover:bg-green-700 text-white px-6 py-3 rounded-lg font-semibold text-lg transition duration-300 ease-in-out transform hover:scale-105 shadow-md"
        >
          Add Product
        </button>
      </form>
      <p className="mt-2 text-sm text-gray-600 text-center">{message}</p>

      <h3 className="text-lg font-semibold mt-8 border-b pb-2 mb-6">Current Products:</h3>
      {localProducts.length === 0 ? (
        <p className="text-center mt-4 text-gray-600">No products in the database. Add one using the form above!</p>
      ) : (
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6 mt-6">
          {localProducts.map((p) => (
            <div
              key={p.id}
              className="bg-gray-50 shadow-md rounded-lg p-4 relative flex flex-col items-center border border-gray-200"
            >
              <button
                onClick={() => handleDelete(p.id)}
                className="absolute top-2 right-2 text-red-500 hover:text-red-700 p-1 rounded-full bg-white hover:bg-red-100 transition duration-200"
                title="Delete Product"
                aria-label={`Delete ${p.title}`}
              >
                üóëÔ∏è
              </button>
              <img
                src={p.image}
                alt={p.title}
                className="w-32 h-auto mx-auto mb-3 rounded-md shadow-sm"
              />
              <h3 className="text-md font-bold text-center mb-1">{p.title}</h3>
              <p className="text-green-600 font-semibold text-center mb-3">{p.price}</p>
              <Link
                to={`/product/${p.id}`}
                className="block text-center text-blue-600 hover:underline text-sm"
              >
                View Details
              </Link>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

/**
 * Main App Component
 * Manages global state (products) and routes.
 */
function App() {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true); // Loading state for initial product fetch
  const [error, setError] = useState(null); // Error state for initial product fetch
  const location = useLocation(); // Hook for current URL location
  const token = new URLSearchParams(location.search).get("token"); // Get token from URL for admin access

  useEffect(() => {
    // Initial fetch of all products when the app loads
    setLoading(true);
    setError(null);
    axios.get(`${API_BASE_URL}/products`)
      .then((res) => {
        // Validate that the response is an array before setting state
        if (Array.isArray(res.data)) {
          setProducts(res.data);
        } else {
          console.error("Backend /products did not return an array for main page:", res.data);
          setError("Error: Expected product list but received unexpected data from server. Check console.");
          setProducts([]); // Ensure products is an empty array to prevent rendering issues
        }
      })
      .catch((err) => {
        console.error("Error fetching initial products for main page:", err);
        setError("Failed to load products. Please check your internet connection or server status.");
      })
      .finally(() => {
        setLoading(false); // End loading regardless of success or failure
      });
  }, []); // Empty dependency array means this effect runs once on initial component mount

  return (
    <div className="min-h-screen bg-gray-100 font-sans text-gray-800">
      <Routes>
        <Route
          path="/"
          element={
            <div className="max-w-6xl mx-auto p-6">
              <header className="flex justify-between items-center mb-8 bg-white p-4 rounded-lg shadow-sm">
                <h1 className="text-3xl font-extrabold text-blue-700">Affiliate Products</h1>
                <Link
                  to="/admin-login"
                  className="text-blue-600 hover:text-blue-800 font-medium transition duration-200 hover:underline"
                >
                  Admin Login
                </Link>
              </header>

              {/* Conditional rendering for loading, error, or product list */}
              {loading ? (
                <p className="text-center mt-10 text-xl text-blue-600">Loading products...</p>
              ) : error ? (
                <p className="text-center mt-10 text-xl text-red-500">{error}</p>
              ) : (
                <ProductList products={products} />
              )}

              <p className="text-xs text-center text-gray-500 mt-12 p-4 bg-white rounded-lg shadow-sm">
                As an Amazon Associate, I earn from qualifying purchases.
              </p>
            </div>
          }
        />
        {/* Route for individual product pages */}
        <Route path="/product/:productId" element={<ProductPage />} />
        {/* Route for admin login page */}
        <Route path="/admin-login" element={<AdminLogin />} />
        {/* Protected route for admin panel, checks for token */}
        <Route
          path="/admin"
          element={
            token === ADMIN_TOKEN ? (
              <AdminPage setProducts={setProducts} />
            ) : (
              <p className="text-center mt-10 text-2xl text-red-600 font-bold">Access Denied: Not authorized. Please login as admin.</p>
            )
          }
        />
      </Routes>
    </div>
  );
}

/**
 * RootApp Component
 * Wraps the main App component with BrowserRouter for routing.
 * This is the component you should render in your index.js or main.js.
 */
export default function RootApp() {
  return (
    <Router>
      <App />
    </Router>
  );
}
