import React, { useState } from "react";
import axios from "axios";
import { BrowserRouter as Router, Routes, Route, Link, useParams } from "react-router-dom";

// ðŸ§± Product listing on homepage
const ProductList = ({ products }) => (
  <div>
    {products.map((p, idx) => (
      <div key={idx} style={{ border: "1px solid #ccc", margin: 10, padding: 10 }}>
        <h3>{p.title}</h3>
        <img src={p.image} alt={p.title} style={{ width: "150px" }} />
        <p>{p.price}</p>
        <Link to={`/product/${p.id}`}>View Product Page</Link>
      </div>
    ))}
  </div>
);

// ðŸ§± Individual product page
const ProductPage = () => {
  const { productId } = useParams();
  const [product, setProduct] = useState(null);

  React.useEffect(() => {
    axios.get(`http://localhost:8000/product/${productId}`).then((res) => {
      setProduct(res.data);
    });
  }, [productId]);

  if (!product) return <p>Loading...</p>;
  if (product.error) return <p>Product not found</p>;

  return (
    <div style={{ padding: 20 }}>
      <h2>{product.title}</h2>
      <img src={product.image} alt={product.title} style={{ width: "200px" }} />
      <p>{product.price}</p>
      <a href={product.url} target="_blank" rel="noreferrer">Buy on Amazon</a>
    </div>
  );
};

// ðŸ§± Main app structure with routing
function App() {
  const [link, setLink] = useState("");
  const [products, setProducts] = useState([]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const res = await axios.post("http://localhost:8000/add-product", { url: link });
    if (!res.data.error) {
      setProducts([res.data, ...products]);
      setLink("");
    } else {
      alert("Failed to add product.");
    }
  };

  return (
    <Router>
      <Routes>
        <Route
          path="/"
          element={
            <div style={{ padding: 20 }}>
              <h1>Amazon Affiliate Auto-Poster</h1>
              <form onSubmit={handleSubmit}>
                <input
                  value={link}
                  onChange={(e) => setLink(e.target.value)}
                  placeholder="Paste Amazon link here"
                  style={{ width: "60%", padding: 10, marginRight: 10 }}
                />
                <button type="submit">Add Product</button>
              </form>
              <ProductList products={products} />
            </div>
          }
        />
        <Route path="/product/:productId" element={<ProductPage />} />
      </Routes>
    </Router>
  );
}

export default App;
