// --- App.js (React frontend) ---
import React, { useState, useEffect } from "react";
import axios from "axios";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Link,
  useParams,
  useNavigate,
} from "react-router-dom";

const ProductList = ({ products }) => (
  <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6 mt-8">
    {products.map((p) => (
      <div key={p.id} className="bg-white shadow-md rounded-lg p-4 flex flex-col items-center">
        <img src={p.image} alt={p.title} className="w-40 h-auto mb-4" />
        <h3 className="text-lg font-semibold text-center">{p.title}</h3>
        <p className="text-green-600 font-bold my-2">{p.price}</p>
        <Link
          to={`/product/${p.id}`}
          className="mt-auto bg-blue-600 hover:bg-blue-700 text-white py-2 px-4 rounded"
        >
          View Product
        </Link>
      </div>
    ))}
  </div>
);

const ProductPage = () => {
  const { productId } = useParams();
  const [product, setProduct] = useState(null);

  useEffect(() => {
    axios.get(`http://localhost:8000/product/${productId}`).then((res) => {
      setProduct(res.data);
    });
  }, [productId]);

  if (!product) return <p className="text-center mt-10">Loading...</p>;
  if (product.error) return <p className="text-center mt-10">Product not found</p>;

  return (
    <div className="max-w-2xl mx-auto mt-10 p-4 bg-white shadow rounded">
      <h2 className="text-2xl font-bold mb-4">{product.title}</h2>
      <img src={product.image} alt={product.title} className="w-full max-w-xs mx-auto mb-4" />
      <p className="text-xl text-green-600 font-semibold mb-2">{product.price}</p>
      <p className="text-sm text-gray-500 mb-2">
        Prices are subject to change and are accurate as of the time of publishing.
      </p>
      <a
        href={product.url}
        target="_blank"
        rel="noreferrer"
        className="block text-center bg-orange-500 hover:bg-orange-600 text-white py-2 rounded"
      >
        Buy on Amazon
      </a>
      <p className="text-xs text-center text-gray-400 mt-4">
        As an Amazon Associate, I earn from qualifying purchases.
      </p>

      <div className="mt-6 bg-gray-50 p-4 rounded border border-gray-200">
        <h4 className="text-lg font-semibold mb-2">AI-Generated Product Summary</h4>
        <p className="text-sm text-gray-700">
          {`Looking for a quick overview? This product is a reliable choice for anyone seeking quality and value. With its appealing design and practical features, it's ideal for both everyday use and gifting. Based on product insights, it's a popular option among Amazon shoppers, offering a solid balance of performance and affordability.`}
        </p>
      </div>
    </div>
  );
};

const AdminLogin = ({ onLogin }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleLogin = () => {
    if (email === "admin@example.com" && password === "supersecure") {
      localStorage.setItem("authenticated", "true");
      onLogin();
      navigate("/admin");
    } else {
      alert("Invalid credentials");
    }
  };

  return (
    <div className="max-w-md mx-auto mt-20 bg-white p-6 shadow rounded">
      <h2 className="text-2xl font-bold mb-4">Admin Login</h2>
      <input
        type="email"
        placeholder="Email"
        className="w-full border mb-3 p-2"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
      />
      <input
        type="password"
        placeholder="Password"
        className="w-full border mb-3 p-2"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <button
        onClick={handleLogin}
        className="w-full bg-blue-600 hover:bg-blue-700 text-white py-2 rounded"
      >
        Login
      </button>
    </div>
  );
};

const AdminPage = ({ products, setProducts }) => {
  const [url, setUrl] = useState("");

  const addProduct = async () => {
    try {
      const res = await axios.post("http://localhost:8000/add-product", { url });
      setProducts([...products, res.data]);
      setUrl("");
    } catch (err) {
      alert("Failed to add product");
      console.error(err);
    }
  };

  const deleteProduct = async (id) => {
    try {
      await axios.delete(`http://localhost:8000/product/${id}`);
      setProducts(products.filter((p) => p.id !== id));
    } catch (err) {
      alert("Failed to delete product");
    }
  };

  return (
    <div className="max-w-4xl mx-auto mt-10 p-4">
      <h2 className="text-2xl font-bold mb-4">Admin Panel</h2>
      <div className="flex gap-2 mb-6">
        <input
          type="text"
          value={url}
          onChange={(e) => setUrl(e.target.value)}
          placeholder="Paste Amazon product URL"
          className="flex-1 border p-2"
        />
        <button
          onClick={addProduct}
          className="bg-green-600 hover:bg-green-700 text-white py-2 px-4 rounded"
        >
          Add Product
        </button>
      </div>

      <ProductList
        products={products.map((p) => ({
          ...p,
          deleteButton: (
            <button
              onClick={() => deleteProduct(p.id)}
              className="text-red-500 absolute top-2 right-2"
            >
              🗑️
            </button>
          ),
        }))}
      />
    </div>
  );
};

function App() {
  const [products, setProducts] = useState([]);
  const [authenticated, setAuthenticated] = useState(false);

  useEffect(() => {
    axios.get("http://localhost:8000/products").then((res) => {
      setProducts(res.data);
    });
    if (localStorage.getItem("authenticated") === "true") {
      setAuthenticated(true);
    }
  }, []);

  return (
    <Routes>
      <Route
        path="/"
        element={
          <div className="max-w-6xl mx-auto p-6">
            <header className="flex justify-between items-center mb-6">
              <h1 className="text-2xl font-bold">Affiliate Products</h1>
              <Link
                to="/admin-login"
                className="text-blue-600 hover:underline"
              >
                Admin Login
              </Link>
            </header>
            <ProductList products={products} />
            <p className="text-xs text-center text-gray-500 mt-8">
              As an Amazon Associate, I earn from qualifying purchases.
            </p>
          </div>
        }
      />
      <Route path="/product/:productId" element={<ProductPage />} />
      <Route path="/admin-login" element={<AdminLogin onLogin={() => setAuthenticated(true)} />} />
      <Route
        path="/admin"
        element={
          authenticated ? (
            <AdminPage products={products} setProducts={setProducts} />
          ) : (
            <p className="text-center mt-10">Not authorized</p>
          )
        }
      />
    </Routes>
  );
}

export default function RootApp() {
  return (
    <Router>
      <App />
    </Router>
  );
}
