import React, { useState } from "react";
import axios from "axios";

function App() {
  const [link, setLink] = useState("");
  const [products, setProducts] = useState([]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post("http://localhost:8000/add-product", { url: link });
      if (!res.data.error) {
        setProducts([res.data, ...products]);
        setLink("");
      } else {
        alert("Product couldn't be added.");
      }
    } catch (err) {
      alert("Server error.");
    }
  };

  return (
    <div style={{ padding: 20 }}>
      <h1>Amazon Affiliate Auto-Poster</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={link}
          onChange={(e) => setLink(e.target.value)}
          placeholder="Paste Amazon link here"
          style={{ width: "60%", padding: 10, marginRight: 10 }}
        />
        <button type="submit">Add Product</button>
      </form>

      <div style={{ marginTop: 30 }}>
        {products.map((p, index) => (
          <div key={index} style={{ border: "1px solid #ccc", margin: 10, padding: 10 }}>
            <h3>{p.title}</h3>
            <img src={p.image} alt={p.title} style={{ width: "150px" }} />
            <p>{p.price}</p>
            <a href={p.url} target="_blank" rel="noreferrer">Buy Now</a>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
