import React, { useState } from "react";
import axios from "axios";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Link,
  useParams,
} from "react-router-dom";

// ðŸ§± Homepage product list
const ProductList = ({ products }) => (
  <div>
    {products.map((p, idx) => (
      <div
        key={idx}
        style={{
          border: "1px solid #ccc",
          margin: 10,
          padding: 10,
          borderRadius: "8px",
        }}
      >
        <h3>{p.title}</h3>
        <img
          src={p.image}
          alt={p.title}
          style={{ width: "150px", height: "auto" }}
        />
        <p>{p.price}</p>
        <Link to={`/product/${p.id}`}>View Product Page</Link>
      </div>
    ))}
  </div>
);

// ðŸ§± Product detail page
const ProductPage = () => {
  const { productId } = useParams();
  const [product, setProduct] = useState(null);

  React.useEffect(() => {
    axios
      .get(`http://localhost:8000/product/${productId}`)
      .then((res) => {
        setProduct(res.data);
      })
      .catch((err) => {
        console.error("Failed to fetch product:", err);
      });
  }, [productId]);

  if (!product) return <p>Loading...</p>;
  if (product.error) return <p>Product not found</p>;

  return (
    <div style={{ padding: 20 }}>
      <h2>{product.title}</h2>
      <img
        src={product.image}
        alt={product.title}
        style={{ width: "200px", height: "auto" }}
      />
      <p>{product.price}</p>
      <a href={product.url} target="_blank" rel="noreferrer">
        Buy on Amazon
      </a>
    </div>
  );
};

// ðŸ§± Main App
function App() {
  const [link, setLink] = useState("");
  const [products, setProducts] = useState([]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post("http://localhost:8000/add-product", {
        url: link,
      });
      console.log("Response:", res.data);

      if (res.data && !res.data.error) {
        setProducts([res.data, ...products]);
        setLink("");
      } else {
        alert("Failed to add product.");
      }
    } catch (err) {
      alert("Server error.");
      console.error("Axios error:", err);
    }
  };

  return (
    <Router>
      <Routes>
        <Route
          path="/"
          element={
            <div style={{ padding: 20 }}>
              <h1>Amazon Affiliate Auto-Poster</h1>
              <form onSubmit={handleSubmit}>
                <input
                  value={link}
                  onChange={(e) => setLink(e.target.value)}
                  placeholder="Paste Amazon link here"
                  style={{
                    width: "60%",
                    padding: 10,
                    marginRight: 10,
                    fontSize: "16px",
                  }}
                />
                <button type="submit" style={{ padding: 10 }}>
                  Add Product
                </button>
              </form>
              <ProductList products={products} />
            </div>
          }
        />
        <Route path="/product/:productId" element={<ProductPage />} />
      </Routes>
    </Router>
  );
}

export default App;
