import React, { useState } from "react";
import axios from "axios";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Link,
  useParams,
  useNavigate,
  useLocation
} from "react-router-dom";

// Simulated admin password
const ADMIN_TOKEN = "letmein";  // Change this to whatever you like

// Product listing on homepage
const ProductList = ({ products }) => (
  <div>
    {products.map((p, idx) => (
      <div
        key={idx}
        style={{
          border: "1px solid #ccc",
          margin: 10,
          padding: 10,
          borderRadius: "8px",
        }}
      >
        <h3>{p.title}</h3>
        <img
          src={p.image}
          alt={p.title}
          style={{ width: "150px", height: "auto" }}
        />
        <p>{p.price}</p>
        <Link to={`/product/${p.id}`}>View Product Page</Link>
      </div>
    ))}
  </div>
);

// Individual product page
const ProductPage = () => {
  const { productId } = useParams();
  const [product, setProduct] = useState(null);

  React.useEffect(() => {
    axios.get(`http://localhost:8000/product/${productId}`).then((res) => {
      setProduct(res.data);
    });
  }, [productId]);

  if (!product) return <p>Loading...</p>;
  if (product.error) return <p>Product not found</p>;

  return (
    <div style={{ padding: 20 }}>
      <h2>{product.title}</h2>
      <img
        src={product.image}
        alt={product.title}
        style={{ width: "200px", height: "auto" }}
      />
      <p>{product.price}</p>
      <a href={product.url} target="_blank" rel="noreferrer">
        Buy on Amazon
      </a>
    </div>
  );
};

// Admin page
const AdminPage = ({ setProducts }) => {
  const [link, setLink] = useState("");
  const [message, setMessage] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post("http://localhost:8000/add-product", {
        url: link,
      });
      if (res.data && !res.data.error) {
        setProducts((prev) => [res.data, ...prev]);
        setLink("");
        setMessage("Product added!");
      } else {
        setMessage("Failed to add product.");
      }
    } catch (err) {
      setMessage("Server error.");
    }
  };

  return (
    <div style={{ padding: 20 }}>
      <h2>Admin Panel</h2>
      <form onSubmit={handleSubmit}>
        <input
          value={link}
          onChange={(e) => setLink(e.target.value)}
          placeholder="Paste Amazon link"
          style={{ width: "60%", padding: 10, marginRight: 10 }}
        />
        <button type="submit" style={{ padding: 10 }}>
          Add Product
        </button>
      </form>
      <p>{message}</p>
    </div>
  );
};

// Admin login page
const AdminLogin = () => {
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleLogin = (e) => {
    e.preventDefault();
    if (password === ADMIN_TOKEN) {
      navigate("/admin?token=" + ADMIN_TOKEN);
    } else {
      alert("Incorrect password.");
    }
  };

  return (
    <div style={{ padding: 20 }}>
      <h2>Admin Login</h2>
      <form onSubmit={handleLogin}>
        <input
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          placeholder="Enter admin password"
          type="password"
          style={{ padding: 10 }}
        />
        <button type="submit" style={{ marginLeft: 10, padding: 10 }}>
          Login
        </button>
      </form>
    </div>
  );
};

// Main App
function App() {
  const [products, setProducts] = useState([]);
  const location = useLocation();
  const token = new URLSearchParams(location.search).get("token");

  return (
    <Routes>
      <Route
        path="/"
        element={
          <div style={{ padding: 20 }}>
            <h1>Affiliate Products</h1>
            <Link to="/admin-login">Admin Login</Link>
            <ProductList products={products} />
          </div>
        }
      />
      <Route path="/product/:productId" element={<ProductPage />} />
      <Route path="/admin-login" element={<AdminLogin />} />
      <Route
        path="/admin"
        element={
          token === ADMIN_TOKEN ? (
            <AdminPage setProducts={setProducts} />
          ) : (
            <p style={{ padding: 20 }}>Not authorized</p>
          )
        }
      />
    </Routes>
  );
}

export default function RootApp() {
  return (
    <Router>
      <App />
    </Router>
  );
}
