// --- App.js (React frontend) ---
import React, { useState, useEffect } from "react";
import axios from "axios";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Link,
  useParams,
  useNavigate,
  useLocation,
} from "react-router-dom";

// IMPORTANT: Define your API base URL using the environment variable.
// This will be populated from your .env file during build.
// The "|| 'http://localhost:8000'" is a fallback for local dev if the env var isn't set,
// but for deployment, you must set REACT_APP_API_BASE_URL in your .env
const API_BASE_URL = process.env.REACT_APP_API_BASE_URL || "http://localhost:8000";

// For security, never commit real tokens to source control.
// In a real app, this would be handled via secure authentication.
const ADMIN_TOKEN = "letmein";

// Component to display a list of products
const ProductList = ({ products }) => {
  if (!products || products.length === 0) {
    return <p className="text-center mt-8 text-gray-600">No products found. Add some from the Admin panel!</p>;
  }

  return (
    <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6 mt-8">
      {products.map((p) => (
        <div
          key={p.id}
          className="bg-white shadow-md rounded-lg p-4 flex flex-col items-center relative"
        >
          <img src={p.image} alt={p.title} className="w-40 h-auto mb-4" />
          <h3 className="text-lg font-semibold text-center">{p.title}</h3>
          <p className="text-green-600 font-bold my-2">{p.price}</p>
          <Link
            to={`/product/${p.id}`}
            className="mt-auto bg-blue-600 hover:bg-blue-700 text-white py-2 px-4 rounded"
          >
            View Product
          </Link>
        </div>
      ))}
    </div>
  );
};

// Component to display a single product page
const ProductPage = () => {
  const { productId } = useParams();
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    setLoading(true);
    setError(null);
    axios.get(`${API_BASE_URL}/product/${productId}`)
      .then((res) => {
        if (res.data && res.data.error) {
          setError(res.data.error);
        } else {
          setProduct(res.data);
        }
      })
      .catch((err) => {
        console.error("Error fetching product:", err);
        setError("Failed to load product. Please try again later.");
      })
      .finally(() => {
        setLoading(false);
      });
  }, [productId]);

  if (loading) return <p className="text-center mt-10">Loading product details...</p>;
  if (error) return <p className="text-center mt-10 text-red-500">{error}</p>;
  if (!product) return <p className="text-center mt-10 text-gray-600">Product not found.</p>; // Should be caught by error, but as a fallback

  return (
    <div className="max-w-2xl mx-auto mt-10 p-4 bg-white shadow rounded">
      <h2 className="text-2xl font-bold mb-4">{product.title}</h2>
      <img
        src={product.image}
        alt={product.title}
        className="w-full max-w-xs mx-auto mb-4"
      />
      <p className="text-xl text-green-600 font-semibold mb-4">{product.price}</p>
      <a
        href={product.url}
        target="_blank"
        rel="noreferrer"
        className="block text-center bg-orange-500 hover:bg-orange-600 text-white py-2 rounded"
      >
        Buy on Amazon
      </a>
      <p className="text-xs text-center text-gray-400 mt-4">
        As an Amazon Associate, I earn from qualifying purchases.
      </p>

      {product.review && (
        <div className="mt-6 bg-white p-4 rounded border border-yellow-200">
          <p className="text-sm text-gray-800 italic">{product.review}</p>
        </div>
      )}
    </div>
  );
};

// Component for Admin Login
const AdminLogin = () => {
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleLogin = (e) => {
    e.preventDefault();
    if (password === ADMIN_TOKEN) {
      // Use navigate directly for security (don't pass token in URL for real apps)
      // For this simple case, we'll keep the token in URL for consistency with your code
      navigate("/admin?token=" + ADMIN_TOKEN);
    } else {
      alert("Incorrect password.");
    }
  };

  return (
    <div className="max-w-md mx-auto mt-20 p-6 bg-white shadow rounded">
      <h2 className="text-xl font-bold mb-4">Admin Login</h2>
      <form onSubmit={handleLogin}>
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          placeholder="Enter admin password"
          className="w-full border px-4 py-2 rounded mb-4"
        />
        <button className="w-full bg-blue-600 hover:bg-blue-700 text-white py-2 rounded">
          Login
        </button>
      </form>
    </div>
  );
};

// Component for Admin Panel
const AdminPage = ({ setProducts }) => {
  const [link, setLink] = useState("");
  const [message, setMessage] = useState("");
  const [localProducts, setLocalProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchProducts = async () => {
    setLoading(true);
    setError(null);
    try {
      const res = await axios.get(`${API_BASE_URL}/products`);
      if (Array.isArray(res.data)) { // Ensure the response is an array
        setLocalProducts(res.data);
        setProducts(res.data);
      } else {
        console.error("Backend /products did not return an array:", res.data);
        setError("Error: Expected product list but received unexpected data.");
        setLocalProducts([]); // Set to empty to avoid rendering issues
      }
    } catch (err) {
      console.error("Failed to fetch products for admin panel:", err);
      setError("Failed to load products. Check server connection.");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage("Adding product..."); // Provide immediate feedback
    try {
      const res = await axios.post(`${API_BASE_URL}/add-product`, {
        url: link,
      });
      if (res.data && !res.data.error) {
        setLocalProducts((prev) => [res.data, ...prev]);
        setProducts((prev) => [res.data, ...prev]);
        setLink("");
        setMessage("✅ Product added successfully!");
      } else {
        setMessage(`❌ Failed to add product: ${res.data.detail || res.data.error || 'Unknown error'}`);
      }
    } catch (err) {
      setMessage("❌ Server error when adding product. See console for details.");
      console.error("Add product error:", err.response ? err.response.data : err.message);
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm("Are you sure you want to delete this product?")) return;
    try {
      await axios.delete(`${API_BASE_URL}/product/${id}`);
      const updated = localProducts.filter((p) => p.id !== id);
      setLocalProducts(updated);
      setProducts(updated);
      setMessage("Product deleted successfully.");
    } catch (err) {
      alert("Failed to delete product.");
      console.error("Delete product error:", err);
      setMessage("❌ Failed to delete product.");
    }
  };

  if (loading) return <p className="text-center mt-10">Loading admin panel products...</p>;
  if (error) return <p className="text-center mt-10 text-red-500">{error}</p>;

  return (
    <div className="max-w-6xl mx-auto mt-10 p-6 bg-white shadow rounded">
      <h2 className="text-xl font-bold mb-4">Admin Panel</h2>
      <form onSubmit={handleSubmit} className="flex gap-2">
        <input
          value={link}
          onChange={(e) => setLink(e.target.value)}
          placeholder="Paste Amazon link"
          className="flex-1 border px-4 py-2 rounded"
          required // Make sure link is provided
        />
        <button type="submit" className="bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded">
          Add Product
        </button>
      </form>
      <p className="mt-2 text-sm text-gray-600">{message}</p>

      <h3 className="text-lg font-semibold mt-8">Current Products:</h3>
      {localProducts.length === 0 ? (
        <p className="text-center mt-4 text-gray-600">No products in the database. Add one above!</p>
      ) : (
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6 mt-6">
          {localProducts.map((p) => (
            <div
              key={p.id}
              className="bg-white shadow-md rounded-lg p-4 relative"
            >
              <button
                onClick={() => handleDelete(p.id)}
                className="absolute top-2 right-2 text-red-500 hover:text-red-700"
                title="Delete"
              >
                🗑️
              </button>
              <img
                src={p.image}
                alt={p.title}
                className="w-40 h-auto mx-auto mb-2"
              />
              <h3 className="text-md font-bold text-center">{p.title}</h3>
              <p className="text-green-600 font-semibold mb-2 text-center">{p.price}</p>
              <Link
                to={`/product/${p.id}`}
                className="block text-center text-blue-600 hover:underline"
              >
                View Details
              </Link>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

// Main App component
function App() {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const location = useLocation();
  const token = new URLSearchParams(location.search).get("token");

  useEffect(() => {
    setLoading(true);
    setError(null);
    axios.get(`${API_BASE_URL}/products`)
      .then((res) => {
        if (Array.isArray(res.data)) { // Ensure the response is an array
          setProducts(res.data);
        } else {
          console.error("Backend /products did not return an array for main page:", res.data);
          setError("Error: Expected product list but received unexpected data from server.");
          setProducts([]); // Set to empty to avoid rendering issues
        }
      })
      .catch((err) => {
        console.error("Error fetching initial products for main page:", err);
        setError("Failed to load products. Please check your internet connection or server status.");
      })
      .finally(() => {
        setLoading(false);
      });
  }, []);

  return (
    <Routes>
      <Route
        path="/"
        element={
          <div className="max-w-6xl mx-auto p-6">
            <header className="flex justify-between items-center mb-6">
              <h1 className="text-2xl font-bold">Affiliate Products</h1>
              <Link
                to="/admin-login"
                className="text-blue-600 hover:underline"
              >
                Admin Login
              </Link>
            </header>

            {loading ? (
              <p className="text-center mt-10">Loading products...</p>
            ) : error ? (
              <p className="text-center mt-10 text-red-500">{error}</p>
            ) : (
              <ProductList products={products} />
            )}

            <p className="text-xs text-center text-gray-500 mt-8">
              As an Amazon Associate, I earn from qualifying purchases.
            </p>
          </div>
        }
      />
      <Route path="/product/:productId" element={<ProductPage />} />
      <Route path="/admin-login" element={<AdminLogin />} />
      <Route
        path="/admin"
        element={
          token === ADMIN_TOKEN ? (
            <AdminPage setProducts={setProducts} />
          ) : (
            <p className="text-center mt-10 text-red-500">Not authorized. Please login as admin.</p>
          )
        }
      />
    </Routes>
  );
}

// Root component to wrap App with Router
export default function RootApp() {
  return (
    <Router>
      <App />
    </Router>
  );
}
